                      $(document).ready(function() {
                      $(function () {
                            var dialogTakedown = $('#takedown').dialog({
                               create: function(event, ui) {
                                    var widget = $(this).dialog("widget");
                                    $(".ui-dialog-titlebar-close span", widget).removeClass("ui-icon-closethick").addClass("ui-icon-myCloseButton");
                                  },
                                  autoOpen: false,
                                  modal: true,
                                  resizable: true,
                                  width: "600",
                                  height: "300",
                                  close: function () {
                                    $('.ui-dialog').hide();
                                  }
                               });
                            $("a.takedown").on("click", function (e) {
                            e.preventDefault();
                              dialogTakedown.dialog("option", "title", '').dialog("open");
                            });
                        });
                      $(function () {
                        var dialogFeedback = $('#feedback').dialog({
                               create: function(event, ui) {
                                    var widget = $(this).dialog("widget");
                                    $(".ui-dialog-titlebar-close span", widget).removeClass("ui-icon-closethick").addClass("ui-icon-myCloseButton");
                                  },
                                  autoOpen: false,
                                  modal: true,
                                  resizable: true,
                                  width: "400",
                                  height: "430",
                                  close: function () {
                                    $('.ui-dialog').hide();
                                  }
                               });
                            $(".feedback-tab").on("click", function (e) {
                            e.preventDefault();
                              dialogFeedback.dialog("option", "title", '').dialog("open");
                            });
                        });
                       $(function () {
                            var iframe = $('');
                            var dialog = $('
').append(iframe).appendTo('body').dialog({
                                create: function(event, ui) {
                                    var widget = $(this).dialog("widget");
                                    $(".ui-dialog-titlebar-close span", widget).removeClass("ui-icon-closethick").addClass("ui-icon-myCloseButton");
                                },
                                autoOpen: false,
                                modal: true,
                                resizable: true,
                                width: "850",
                                height: "600",
                                close: function () {
                                    iframe.attr("src", "");
                                    $('.daoCitation').remove();
                                }
                            });

                            var dialogDsc = $('
').appendTo('body').dialog({
                               create: function(event, ui) {
                                    var widget = $(this).dialog("widget");
                                    $(".ui-dialog-titlebar-close span", widget).removeClass("ui-icon-closethick").addClass("ui-icon-myCloseButton");
                                  },
                                  autoOpen: false,
                                  modal: true,
                                  resizable: true,
                                  width: "550",
                                  height: "300",
                                  close: function () {
                                    $('.ui-dialog .dscDescription').remove();
                                  }
                               });

                            $(".daoLink a").on("click", function (e) {
                                e.preventDefault();
                                var src = $(this).attr("href");
                                var title = $(this).attr("data-title");
                                var width = $(this).attr("data-width");
                                var height = $(this).attr("data-height");
                                var citation = $(this).attr("data-citation");
                                var buildCitation = $('
').append(citation)
                                iframe.attr({
                                    width: +width,
                                    height: +height,
                                    src: src
                                });
                                dialog.dialog("option", "title", title).dialog("open").before(buildCitation);
                            });

                            $("a.dialog_dsc").on("click", function (e) {
                              var id = $(this).attr("href");
                              var content = $(id).html()
                              dialogDsc.dialog("option", "title", '').dialog("open").append(content);
                            });

                            //checks for hash tag jumps to location and opens appropriate diolog 
                            if(window.location.hash) {
                              var hash = window.location.hash.substring(1); //Puts hash in variable, and removes the # character
                              $("#" + hash + " .daoLink a").each(function (e) {
                                 var src = $(this).attr("href");
                                 var title = $(this).attr("data-title");
                                 var citation = $(this).attr("data-citation");
                                 var width = $(this).attr("data-width");
                                 var height = $(this).attr("data-height");
                                 var buildCitation = $('
').append(citation)
                                    iframe.attr({
                                    width: +width,
                                    height: +height,
                                    src: src
                                 });
                                 dialog.dialog("option", "title", title).dialog("open").before(buildCitation);
                              });
                            } 

                        });
                    });
